import React, { useEffect, useRef } from "react";
import { getToken } from "../../shared/TokenProvider.ts";


async function getUserId()
{
  const token= await getToken();
  const Url="https://localhost:7001/"+token;
  let response = await fetch(Url,
  {
    method: "GET",
    headers:     
    {
      'Accept': 'application/json',
      'Authorization': "Bearer " + token,
      'Content-Type': 'application/json'
    },
  });
  console.log(response);
  const j = await response.json();
  return j;
}

const DropDown = () => {
  const [open, setOpen] = React.useState(false);
  const [id, setId] = React.useState(0);
  const ref = useRef(null); 

  useEffect(() => {
    const handleClickOutside = (event) => ref.current.contains(event.target) || console.log('Клик вне компонента');


    document.addEventListener('mousedown', handleClickOutside);
    return () => {
        document.removeEventListener('mousedown', handleClickOutside);
    };
}, []);

  const handleOpen = () => {
    setOpen(!open);
  };
  const get = async () =>{
    //const data = await getUserId()
    //setId(data.id);
  }
  get();
  return (
    <div className="dropdown">
      <button onClick={handleOpen} className="menuButton"></button>
      {open ? (
        <ul className="menu">
          <li>
            <a href={"profile/"+id}>Профиль</a>
          </li>
          <li>
          <a href="selfWishs">Список желаний</a>
          </li>
          <li>
          <a href="add">Добавить желание</a>
          </li>
          <li>
          <a href="vda">Выйти</a>
          </li>
        </ul>
      ) : null}
    </div>
  );
};

export default DropDown;
