import React from "react";

const url="http://localhost:5140/User/SingUp";


async function PostUsers(name, email, pass)
{
  const response = await fetch(url,
  {
    method: "POST",
    headers:     
    {
      'Accept': 'application/json',
      'Content-Type': 'application/json'
    },
    body: JSON.stringify({
      name: name,
      email: email,
      password: pass
    })
  });
  if(response.ok)
  {
    let json = await response.json();
    sessionStorage.setItem(tokenKey, json.token);
    const t =sessionStorage.getItem(tokenKey);
    console.log(t);
  }
}


function FormToSingUp()
{
    const[name, setName]=React.useState("");
    const[email, setEmail]=React.useState("");
    const[pass, setPass]=React.useState("");

    const isValide = () =>{
      if (name.length<2)
        return false;
      if (email.length<2)
        return false;
      if (pass.length<2)
        return false;
      return true;
    }
    const handleSubmit = (e) => {
      e.preventDefault();
      if(isValide())
        PostUsers(name, email, pass);
      else
        console.log("введите все поля");

    }


    return (
      <div className="F">
        <form onSubmit={handleSubmit}>
          <p>
            <label>Имя </label>
            <input type="text" value={name} onChange={(e) => setName(e.target.value)}></input>
          </p>
          <p>
            <label>Почта </label>
            <input type="text" value={email} onChange={(e) => setEmail(e.target.value)}></input>
          </p>
          <p>
            <label>Пароль </label>
            <input type="password" value={pass} onChange={(e) => setPass(e.target.value)}></input>
          </p>
          <button type="Submit">Зарегистрироваться</button>
        </form>
      </div>
    );
  }
    
    export default FormToSingUp;
